var n="/* --- Headlines --- */\n.header h1 {\n  font-family: var(--font-light);\n  font-size: 2em;\n  margin: 0 0 0.5em;\n  padding: 0.5em 0 0;\n}\n\n@media screen and (max-width: 320px) {\n  .header h1 {\n    font-size: 1.6em;\n    padding: 0.5em 0;\n    text-align: center;\n  }\n}\n\n.header h2 {\n  margin: 0.3em 0.2em 0;\n  padding: 0;\n  font-size: 1.2em;\n}\n\n@media screen and (max-width: 320px) {\n  .header h2 {\n    display: none;\n  }\n}\n\n/* --- Main Navigation --- */\n.header nav > ul {\n  position: absolute;\n  z-index: 100;\n  top: calc(var(--nav-height) - 36px);\n  left: 0;\n  font-family: var(--font-semi);\n  font-weight: normal;\n  list-style: none;\n  list-style-type: none;\n  margin: 0 auto;\n  padding: 1px 0 0;\n  background-color: var(--color-highlight-1);\n  width: 100%;\n}\n\n.header nav > ul li {\n  display: inline-block;\n  position: relative;\n  margin: 0.3em 0.75em;\n}\n\n@media screen and (min-width: 799px) {\n  .header nav > ul li:hover ul {\n    display: block;\n  }\n}\n\n.header nav > ul ul {\n  display: none;\n  position: absolute;\n  left: -1em;\n  background-color: var(--color-highlight-1);\n  z-index: 1000;\n  padding-left: 0;\n  min-width: 200px;\n}\n\n.header nav > ul ul li {\n  display: block;\n  white-space: nowrap;\n}\n\n@media screen and (max-width: 799px) {\n  .header nav > ul {\n    display: none;\n    top: var(--nav-height);\n  }\n\n  .header nav > ul.active {\n    display: block;\n    padding-bottom: 0.5em;\n  }\n\n  .header nav > ul li {\n    display: block;\n    margin: 0.5em;\n    padding-top: 0.2em;\n    padding-bottom: 0.2em;\n  }\n\n  .header nav > ul li ul.active {\n    display: block;\n    position: relative;\n    padding-left: 1.5em;\n  }\n}\n\n@media screen and (max-width: 320px) {\n  .header nav > ul {\n    top: 110px;\n  }\n}\n\n.header nav a:link {\n  color: var(--color-light);\n}\n\n.header nav a:visited {\n  color: var(--color-light);\n}\n\n.header nav a:hover, .header nav a:active {\n  color: var(--color-light);\n  text-decoration: underline;\n}\n\n/* mobile navigation */\n.header nav .mobile-nav {\n  display: none;\n}\n\n@media screen and (max-width: 799px) {\n  .header nav .mobile-nav {\n    position: absolute;\n    z-index: 100;\n    top: calc(var(--nav-height) - 36px);\n    display: block;\n    margin: 0;\n    padding: 0;\n    width: 30px;\n    height: 36px;\n    word-wrap: nowrap;\n  }\n\n  .header nav .mobile-nav a {\n    display: block;\n    overflow: hidden;\n    word-wrap: nowrap;\n    width: 100%;\n    height: 100%;\n    background: url('/resources/img/menu.png') 0 3px no-repeat transparent;\n    text-indent: 100%;\n  }\n}\n\n@media screen and (max-width: 320px) {\n  .header nav .mobile-nav {\n    top: 76px;\n    margin-left: 5px;\n  }\n}\n\n";export{n as css,n as default};
