var n="/* --- Speaker Gallery --- */\n.speaker-gallery .speakers {\n  display: flex;\n  flex-flow: row wrap;\n  width: 100%;\n  margin-bottom: 15px;\n}\n\n.speaker-gallery .speaker {\n  width: 100%;\n  padding: 10px;\n}\n\n@media screen and (min-width: 320px) {\n  .speaker-gallery .speaker {\n    width: calc(100% / 2);\n  }\n}\n\n@media screen and (min-width: 600px) {\n  .speaker-gallery .speaker {\n    width: calc(100% / 3);\n  }\n}\n\n@media screen and (min-width: 799px) {\n  .speaker-gallery .speaker {\n    width: calc(100% / 4);\n  }\n}\n\n.speaker-gallery .speaker img {\n  width: 100%;\n  aspect-ratio: 1;\n  object-fit: cover;\n}\n\n.speaker-gallery .speaker .name {\n  font-family: var(--font-semi);\n  margin-top: 5px;\n}\n\n.speaker-gallery .speaker .twitter {\n  font-size: smaller;\n}\n\n.speaker-gallery .speaker .affiliation {\n  margin-top: 5px;\n}\n\n/* --- Lightning Talk Speakers --- */\n.speaker-gallery h4 {\n  margin-bottom: 0;\n  margin-bottom: 10px;\n}\n\n.speaker-gallery .speakers.lightning-talk {\n  display: block;\n}\n\n.speaker-gallery .speakers.lightning-talk .speaker {\n  display: flex;\n  flex-flow: row nowrap;\n  align-items: center;\n  gap: 10px;\n  width: 100%;\n  padding: 0;\n}\n\n.speaker-gallery .speakers.lightning-talk .speaker img {\n  width: 40px;\n  height: 40px;\n  object-fit: cover;\n}\n\n.speaker-gallery .speakers.lightning-talk .speaker .name {\n  font-family: var(--font-light);\n}\n\n.speaker-gallery .speakers.lightning-talk .speaker .twitter {\n  display: none;\n}\n";export{n as css,n as default};
